// SPDX-License-Identifier: MIT
pragma solidity ^0.8.0;

import './Context.sol';
import './ERC20.sol';

/**
 * @title Decentraloan Token 
 */

contract DecentraLoanToken is Context, ERC20 {

    mapping(address => uint256) _balances;
    mapping (address => mapping (address => uint256)) internal _allowances;

    string public _name;
    string public _symbol;
    uint256 public _totalSupply;

    /**
     * Constructor
     */
    constructor(string memory coinName, string memory coinSymbol, uint256 initialSupply) {
        _name = coinName;
        _symbol = coinSymbol;

        _mint(msg.sender, initialSupply*(10**decimals()));
    }

    /**
     * Return name of token
     */
    function name() public view virtual returns (string memory) {
        return _name;
    }

    /**
     * Returns the symbol of the token, usually a shorter version of the
     * name.
     */
    function symbol() public view virtual returns (string memory) {
        return _symbol;
    }

    /**
     * Return decimals (default 18 for ERC20 compliant tokens)
     */
    function decimals() public view virtual returns (uint8) {
        return 18;
    }

    /**
     * Total Supply
     */
    function totalSupply() public view override virtual returns (uint) {
        return _totalSupply;
    }

    /**
     * Mint new tokens and send to msg.sender address
     */
    // function print(uint amount) public virtual returns (uint) {
    //     _mint(msg.sender, amount*(10**decimals())); // call internal _mint() method
    //     return _totalSupply;
    // }

    /**
     * Get token balance of a token owner
     */
    function balanceOf(address account) public view override virtual returns (uint balance) {
        return _balances[account];
    }

    /**
     * @dev See {IERC20-transfer}.
     *
     * Requirements:
     *
     * - `recipient` cannot be the zero address.
     * - the caller must have a balance of at least `amount`.
     */
    function transfer(address recipient, uint256 amount) public override virtual returns (bool) {
        _transfer(_msgSender(), recipient, amount);
        return true;
